import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { supabase } from '../supabase';
import './SignIn.css';

const SignIn = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [rememberMe, setRememberMe] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('Enter your details to sign-in');
  const [connectionStatus, setConnectionStatus] = useState('Testing connection...');
  const navigate = useNavigate();

  // Test Supabase connection on component mount
  useEffect(() => {
    testSupabaseConnection();
  }, []);

  // Load saved credentials on component mount
  useEffect(() => {
    const savedEmail = localStorage.getItem('rememberedEmail');
    const savedPassword = localStorage.getItem('rememberedPassword');
    const savedRememberMe = localStorage.getItem('rememberMe') === 'true';

    if (savedRememberMe && savedEmail && savedPassword) {
      setEmail(savedEmail);
      setPassword(savedPassword);
      setRememberMe(true);
    }
  }, []);

  const testSupabaseConnection = async () => {
    try {
      // Test basic connection by getting the current user (should return null if not authenticated)
      const { data, error } = await supabase.auth.getUser();

      if (error) {
        console.error('Supabase connection error:', error);
        setConnectionStatus('‚ùå Connection failed: ' + error.message);
      } else {
        console.log('Supabase connection successful');
        setConnectionStatus('‚úÖ Connected to Supabase');

        // Test database access by trying to query the users table
        try {
          const { data: tableData, error: tableError } = await supabase
            .from('users')
            .select('count')
            .limit(1);

          if (tableError) {
            console.error('Table access error:', tableError);
            setConnectionStatus('‚ö†Ô∏è Connected but table access issue: ' + tableError.message);
          } else {
            setConnectionStatus('‚úÖ Connected to Supabase and database');
          }
        } catch (tableError) {
          console.error('Table test error:', tableError);
          setConnectionStatus('‚ö†Ô∏è Connected but database access issue');
        }
      }
    } catch (error) {
      console.error('Connection test error:', error);
      setConnectionStatus('‚ùå Connection failed: ' + error.message);
    }
  };

  const handleSignIn = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setErrorMessage('Enter your details to sign-in');

    try {
      const { data, error } = await supabase.auth.signInWithPassword({
        email: email,
        password: password,
      });

      if (error) {
        if (error.message.includes('Invalid login credentials')) {
          setErrorMessage('Invalid email or password. Please try again.');
        } else if (error.message.includes('Email not confirmed')) {
          setErrorMessage('Please verify your email address before signing in.');
        } else {
          setErrorMessage('An error occurred during sign in. Please try again.');
        }
        return;
      }

      if (data.user) {
        console.log('‚úÖ User signed in successfully:', data.user.email);
        console.log('User ID:', data.user.id);

        // Handle remember me functionality
        if (rememberMe) {
          localStorage.setItem('rememberedEmail', email);
          localStorage.setItem('rememberedPassword', password);
          localStorage.setItem('rememberMe', 'true');
        } else {
          localStorage.removeItem('rememberedEmail');
          localStorage.removeItem('rememberedPassword');
          localStorage.removeItem('rememberMe');
        }

        // Check if user exists in our custom tables, if not, create them
        try {
          console.log('üîç Checking if user exists in custom tables...');

          const { data: existingUser, error: userCheckError } = await supabase
            .from('users')
            .select('*')
            .eq('id', data.user.id)
            .single();

          if (userCheckError && userCheckError.code === 'PGRST116') {
            // User doesn't exist in our table, create them
            console.log('‚ö†Ô∏è User not found in custom table, creating...');

            const { error: userInsertError } = await supabase
              .from('users')
              .insert({
                id: data.user.id,
                email: data.user.email,
                username: data.user.user_metadata?.username || email.split('@')[0],
                date_joined: new Date().toISOString()
              });

            if (userInsertError) {
              console.error('‚ùå Error creating user in custom table:', userInsertError);
              setErrorMessage('Account created but there was an issue with user data. Please contact support.');
              return;
            } else {
              console.log('‚úÖ User created in custom table successfully');
            }
          } else if (userCheckError) {
            console.error('‚ùå Error checking user data:', userCheckError);
          } else {
            console.log('‚úÖ User already exists in custom table');
          }

          // Also check if profile exists
          console.log('üîç Checking if profile exists...');

          const { data: existingProfile, error: profileCheckError } = await supabase
            .from('user_profiles')
            .select('*')
            .eq('user_id', data.user.id)
            .single();

          if (profileCheckError && profileCheckError.code === 'PGRST116') {
            // Profile doesn't exist, create a default one
            console.log('‚ö†Ô∏è Profile not found, creating default profile...');

            const { error: profileInsertError } = await supabase
              .from('user_profiles')
              .insert({
                user_id: data.user.id,
                gender: 'Other',
                country: 'Unknown',
                dob: new Date('1990-01-01').toISOString()
              });

            if (profileInsertError) {
              console.error('‚ùå Error creating profile:', profileInsertError);
              // Don't fail the sign-in, just log the error
            } else {
              console.log('‚úÖ Default profile created successfully');
            }
          } else if (profileCheckError) {
            console.error('‚ùå Error checking profile data:', profileCheckError);
          } else {
            console.log('‚úÖ Profile already exists in custom table');
          }
        } catch (syncError) {
          console.error('‚ùå Error syncing user data:', syncError);
          // Don't fail the sign-in, just log the error
        }

        // Update last_login in the users table
        try {
          console.log('üîÑ Updating last_login...');

          const { error: updateError } = await supabase
            .from('users')
            .update({ last_login: new Date().toISOString() })
            .eq('id', data.user.id);

          if (updateError) {
            console.error('‚ùå Error updating last_login:', updateError);
          } else {
            console.log('‚úÖ Last login updated successfully');
          }
        } catch (updateError) {
          console.error('‚ùå Error updating last_login:', updateError);
        }

        console.log('üöÄ Navigating to home page...');
        // Navigate to home page
        navigate('/home');
      }
    } catch (error) {
      console.error('‚ùå Sign in error:', error);
      setErrorMessage('An unexpected error occurred. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="signin-container">
      <div className="signin-card">
        <div className="signin-header">
          <h1>Welcome back</h1>
          <p className="signin-subtitle">{errorMessage}</p>
          <div className="connection-status">
            <small>{connectionStatus}</small>
          </div>
        </div>

        <form onSubmit={handleSignIn} className="signin-form">
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your email"
              required
              className="form-input"
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              required
              className="form-input"
            />
          </div>

          <div className="form-options">
            <label className="checkbox-container">
              <input
                type="checkbox"
                checked={rememberMe}
                onChange={(e) => setRememberMe(e.target.checked)}
                className="checkbox-input"
              />
              <span className="checkmark"></span>
              Remember me
            </label>
          </div>

          <button
            type="submit"
            disabled={isLoading}
            className="signin-button"
          >
            {isLoading ? 'Signing in...' : 'Sign in'}
          </button>
        </form>

        <div className="signin-footer">
          <p>
            Don't have an account?{' '}
            <Link to="/signup" className="link">
              Sign up
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default SignIn;
